--删除表
drop table dormitorycopy;
drop table dormitoryfee;
drop table student;
drop table dormitory;
--房间信息表
create table Dormitory
(
  DORMITORYID     INTEGER not null　primary key,
  DORMITORYNAME   VARCHAR2(16),
  BUILDINGID      INTEGER,
  DORMITORYRENT   INTEGER,
  ELECPRICE       NUMBER,
  DORMITORYSTATUS CHAR(1)
);



--学员信息表
create table student
(
  STUID            VARCHAR2(12) not null primary key,
  STUNAME          VARCHAR2(16) not null,
  STUSEX           VARCHAR2(3) not null,
  DORMITORYINDATE  DATE,
  DORMITORYOUTDATE DATE,
  DORMITORYID      INTEGER
);
alter table student
  add constraint FK_student_dormitoryid foreign key (DORMITORYID)
  references DORMITORY (DORMITORYID);


--房间各月份抄表
--房间各月份抄表
create table DORMITORYCOPY
(
  DORMITORYCOPYID INTEGER not null,
  DORMITORYID     INTEGER REFERENCES DORMITORY (DORMITORYID),
  dormitoryname	varchar2(30),
  buildingid int,
  MONTHLY         DATE,
  COPYNUM         INTEGER,
  COPYDATE       DATE
);

create sequence seq_dormitorycopy;



--宿舍费用表
CREATE SEQUENCE SEQ_DORMITORYFEE;
create table DORMITORYFEE
(
  DORMITORYFEEID     INT PRIMARY KEY,
  STUID            VARCHAR2(12) not null,
  STUNAME          VARCHAR2(16) not null,
  STUSEX           VARCHAR2(3) not null,
  MONTHLY          DATE,
  BUILDINGID       INTEGER,
  DORMITORYID      INTEGER not null,
  DORMITORYNAME    VARCHAR2(16),
